mapper.select:
    SELECT *
    FROM (
    SELECT ROWNUM as rnum, b.*
	FROM (
	SELECT a.PRJ_SEQ
	FROM PROJECT_INFO a
	RIGHT OUTER JOIN (SELECT PRJ_SEQ
	, LISTAGG(SK_CD, ',') WITHIN GROUP(ORDER BY SK_CD) AS SKILLS
	FROM (
	SELECT *
	FROM PROJECT_SKILL_TABLE

		WHERE SK_CD IN  #{item} 

	)
	GROUP BY PRJ_SEQ) b
	ON a.PRJ_SEQ = b.PRJ_SEQ
	WHERE 1=1

		AND SKILLS = #{projectDTO.skills}


		AND a.PRJ_SEQ = #{projectDTO.prjSeq}


		AND a.PRJ_NM like '%' || #{projectDTO.prjNm} || '%'


		AND a.CUS_CD = #{projectDTO.cusCD}


		AND a.PRJ_ST_DT BETWEEN #{projectDTO.minSTDT} AND #{projectDTO.maxSTDT}


		AND a.PRJ_ST_DT  <=  #{projectDTO.maxSTDT}


		AND a.PRJ_ST_DT  >=  #{projectDTO.minSTDT}


		AND a.PRJ_ED_DT BETWEEN #{projectDTO.minEDDT} AND #{projectDTO.maxEDDT}


		AND a.PRJ_ED_DT  <=  #{projectDTO.maxEDDT}


		AND a.PRJ_ED_DT  >=  #{projectDTO.minEDDT}

	) a

	LEFT OUTER JOIN 

	(
	SELECT a.*, b.SKILLS
	FROM PROJECT_INFO a
	LEFT OUTER JOIN (SELECT PRJ_SEQ
	     , LISTAGG(SK_CD, ',') WITHIN GROUP(ORDER BY SK_CD) AS SKILLS
	FROM PROJECT_SKILL_TABLE
	GROUP BY PRJ_SEQ
	) b
	ON a.PRJ_SEQ = b.PRJ_SEQ
	) b
	ON a.PRJ_SEQ = b.PRJ_SEQ
	ORDER BY a.PRJ_SEQ
	)
	WHERE RNUM >= #{startNum} and RNUM <= #{endNum}

    SELECT COUNT(*)
	FROM (
    SELECT *
	FROM (
	SELECT a.PRJ_SEQ
	FROM PROJECT_INFO a
	RIGHT OUTER JOIN (SELECT PRJ_SEQ
	, LISTAGG(SK_CD, ',') WITHIN GROUP(ORDER BY SK_CD) AS SKILLS
	FROM (
	SELECT *
	FROM PROJECT_SKILL_TABLE

		WHERE SK_CD IN  #{item} 

	)
	GROUP BY PRJ_SEQ) b
	ON a.PRJ_SEQ = b.PRJ_SEQ
	WHERE 1=1

		AND SKILLS = #{projectDTO.skills}


		AND a.PRJ_SEQ = #{projectDTO.prjSeq}


		AND a.PRJ_NM like '%' || #{projectDTO.prjNm} || '%'


		AND a.CUS_CD = #{projectDTO.cusCD}


		AND a.PRJ_ST_DT BETWEEN #{projectDTO.minSTDT} AND #{projectDTO.maxSTDT}


		AND a.PRJ_ST_DT  <=  #{projectDTO.maxSTDT}


		AND a.PRJ_ST_DT  >=  #{projectDTO.minSTDT}


		AND a.PRJ_ED_DT BETWEEN #{projectDTO.minEDDT} AND #{projectDTO.maxEDDT}


		AND a.PRJ_ED_DT  <=  #{projectDTO.maxEDDT}


		AND a.PRJ_ED_DT  >=  #{projectDTO.minEDDT}

	) a

	LEFT OUTER JOIN 

	(
	SELECT a.*, b.SKILLS
	FROM PROJECT_INFO a
	LEFT OUTER JOIN (SELECT PRJ_SEQ
	     , LISTAGG(SK_CD, ',') WITHIN GROUP(ORDER BY SK_CD) AS SKILLS
	FROM PROJECT_SKILL_TABLE
	GROUP BY PRJ_SEQ
	) b
	ON a.PRJ_SEQ = b.PRJ_SEQ
	) b
	ON a.PRJ_SEQ = b.PRJ_SEQ
	ORDER BY a.PRJ_SEQ
	)
