mapper.select:
SELECT *
FROM (
	SELECT ROWNUM as rnum, g.*
	FROM (
		SELECT c.PRJ_SEQ, c.PRJ_NM, c.CUS_CD, c.PRJ_ST_DT, c.PRJ_ED_DT, d.SKILLS
		FROM (
		    SELECT b.PRJ_SEQ, b.PRJ_NM, b.CUS_CD, b.PRJ_ST_DT, b.PRJ_ED_DT
		    FROM
		    (
		        SELECT *
		        FROM USER_PROJECT_TABLE
		        WHERE USR_SEQ = #{userProjectDTO.usrSeq}
		    ) a
		    RIGHT OUTER JOIN
		    (
		        SELECT *
		        FROM PROJECT_INFO
		    ) b
		    ON a.PRJ_SEQ = b.PRJ_SEQ
		    WHERE USR_SEQ IS NULL

				AND b.PRJ_NM like '%' || #{userProjectDTO.prjNm} || '%'


				AND b.PRJ_SEQ = #{userProjectDTO.prjSeq}


				AND b.CUS_CD = #{userProjectDTO.cusCD}


				AND b.PRJ_ST_DT BETWEEN #{userProjectDTO.minSTDT} AND #{userProjectDTO.maxSTDT}


				AND b.PRJ_ST_DT  <=  #{userProjectDTO.maxSTDT}


				AND b.PRJ_ST_DT  >=  #{userProjectDTO.minSTDT}


				AND b.PRJ_ED_DT BETWEEN #{userProjectDTO.minEDDT} AND #{userProjectDTO.maxEDDT}


				AND b.PRJ_ED_DT  <=  #{userProjectDTO.maxEDDT}


				AND b.PRJ_ED_DT  >=  #{userProjectDTO.minEDDT}

		) c
		RIGHT OUTER JOIN
		(
		    SELECT f.PRJ_SEQ, f.SKILLS
		    FROM 
		    (
		        SELECT PRJ_SEQ
		         , LISTAGG(SK_CD, ',') WITHIN GROUP(ORDER BY SK_CD) AS SKILLS
		         FROM
		        (
		            SELECT *
		            FROM PROJECT_SKILL_TABLE
		        	WHERE SK_CD IN  #{item} 
		    	)
		    	GROUP BY PRJ_SEQ
		    ) e

		    LEFT OUTER JOIN

		    (
		        SELECT PRJ_SEQ
		        , LISTAGG(SK_CD, ',') WITHIN GROUP(ORDER BY SK_CD) AS SKILLS
		        FROM
		            PROJECT_SKILL_TABLE
		        GROUP BY PRJ_SEQ
		    ) f
		    ON e.PRJ_SEQ = f.PRJ_SEQ
		) d
		ON c.PRJ_SEQ = d.PRJ_SEQ
		WHERE c.PRJ_SEQ IS NOT NULL
	) g
)
WHERE RNUM >= #{startNum} and RNUM <= #{endNum}

    SELECT COUNT(*)
	FROM (
	    SELECT c.PRJ_SEQ, c.PRJ_NM, c.CUS_CD, c.PRJ_ST_DT, c.PRJ_ED_DT, d.SKILLS
		FROM (
		    SELECT b.PRJ_SEQ, b.PRJ_NM, b.CUS_CD, b.PRJ_ST_DT, b.PRJ_ED_DT
		    FROM
		    (
		        SELECT *
		        FROM USER_PROJECT_TABLE
		        WHERE USR_SEQ = #{userProjectDTO.usrSeq}
		    ) a
		    RIGHT OUTER JOIN
		    (
		        SELECT *
		        FROM PROJECT_INFO
		    ) b
		    ON a.PRJ_SEQ = b.PRJ_SEQ
		    WHERE USR_SEQ IS NULL

				AND b.PRJ_NM like '%' || #{userProjectDTO.prjNm} || '%'


				AND b.PRJ_SEQ = #{userProjectDTO.prjSeq}


				AND b.CUS_CD = #{userProjectDTO.cusCD}


				AND b.PRJ_ST_DT BETWEEN #{userProjectDTO.minSTDT} AND #{userProjectDTO.maxSTDT}


				AND b.PRJ_ST_DT  <=  #{userProjectDTO.maxSTDT}


				AND b.PRJ_ST_DT  >=  #{userProjectDTO.minSTDT}


				AND b.PRJ_ED_DT BETWEEN #{userProjectDTO.minEDDT} AND #{userProjectDTO.maxEDDT}


				AND b.PRJ_ED_DT  <=  #{userProjectDTO.maxEDDT}


				AND b.PRJ_ED_DT  >=  #{userProjectDTO.minEDDT}

		) c
		RIGHT OUTER JOIN
		(
		    SELECT f.PRJ_SEQ, f.SKILLS
		    FROM 
		    (
		        SELECT PRJ_SEQ
		         , LISTAGG(SK_CD, ',') WITHIN GROUP(ORDER BY SK_CD) AS SKILLS
		         FROM
		        (
		            SELECT *
		            FROM PROJECT_SKILL_TABLE
		        	WHERE SK_CD IN  #{item} 
		    	)
		    	GROUP BY PRJ_SEQ
		    ) e

		    LEFT OUTER JOIN

		    (
		        SELECT PRJ_SEQ
		        , LISTAGG(SK_CD, ',') WITHIN GROUP(ORDER BY SK_CD) AS SKILLS
		        FROM
		            PROJECT_SKILL_TABLE
		        GROUP BY PRJ_SEQ
		    ) f
		    ON e.PRJ_SEQ = f.PRJ_SEQ
		) d
		ON c.PRJ_SEQ = d.PRJ_SEQ
		WHERE c.PRJ_SEQ IS NOT NULL
	)

mapper.insert:
INSERT INTO
	USER_PROJECT_TABLE (
		USR_SEQ,
		PRJ_SEQ
	)
VALUES (
	#{usrSeq},
	#{prjSeq}
)
